dotfiles.plugins/power.sh:
#!/bin/bash

BATT_PERCENT=$(pmset -g batt | grep -Eo "\d+%" | cut -d% -f1)
CHARGING=$(pmset -g batt | grep 'AC Power')

if [[ $CHARGING != "" ]]; then
  sketchybar -m --set battery \
    icon.color=0xFFEBCB8B \
    icon= \
    label=$(printf "${BATT_PERCENT}%%")
  exit 0
fi

[[ ${BATT_PERCENT} -gt 10 ]] && COLOR=0xFF${color5:1} || COLOR=0xFFFF0000

case ${BATT_PERCENT} in
   100) ICON="" ;;
    9[0-9]) ICON="" ;;
    8[0-9]) ICON="" ;;
    7[0-9]) ICON="" ;;
    6[0-9]) ICON="" ;;
    5[0-9]) ICON="" ;;
    4[0-9]) ICON="" ;;
    3[0-9]) ICON="" ;;
    2[0-9]) ICON="" ;;
    1[0-9]) ICON="" ;;
    *) ICON=""
esac

sketchybar -m --set battery\
  icon.color=$COLOR \
  icon= \
  label=$(printf "${BATT_PERCENT}%%")

dotfiles.plugins/space.sh:
#!/usr/bin/env bash

update() {
if [ "$SELECTED" = "true" ]; then
  sketchybar -m --set $NAME label.highlight=on icon.highlight=on background.drawing=on
else
  sketchybar -m --set $NAME label.highlight=off icon.highlight=off background.drawing=off
fi
}

mouse_entered() {
  sketchybar -m --set $NAME icon.highlight=on \
                            label.highlight=on
}

mouse_exited() {
  sketchybar -m --set $NAME icon.highlight=off \
                            label.highlight=off
}

case "$SENDER" in
  "mouse.entered") mouse_entered
  ;;
  "mouse.exited") mouse_exited
  ;;
  *) update 
  ;;
esac

dotfiles.plugins/yabai_float.sh:
#!/usr/bin/env bash

case "$(yabai -m query --windows --window | jq .floating)" in
    0)
    sketchybar -m --set yabai_float icon=""
    ;;
    1)
    sketchybar -m --set yabai_float icon=""
    ;;
esac

dotfiles.plugins/yabai_float_click.sh:
#!/usr/bin/env bash

yabai -m window --toggle float
sketchybar -m --trigger float_change

dotfiles.plugins/yabai_mode.sh:
#!/usr/bin/env bash

case "$(yabai -m query --spaces --display | jq -r 'map(select(."focused" == 1))[-1].type')" in
    bsp)
    sketchybar -m --set yabai_mode icon=""
    ;;
    stack)
    sketchybar -m --set yabai_mode icon="﯅"
    ;;
    float)
    sketchybar -m --set yabai_mode icon=""
    ;;
esac

dotfiles.plugins/yabai_mode_click.sh:
#!/usr/bin/env bash

# Add mode changed event

case "$(yabai -m query --spaces --display | jq -r 'map(select(."focused" == 1))[-1].type')" in
    bsp)
    yabai -m config layout stack && sketchybar -m --set yabai_mode label="﯅"
    ;;
    stack)
    yabai -m config layout float && sketchybar -m --set yabai_mode label=""
    ;;
    float)
    yabai -m config layout bsp && sketchybar -m --set yabai_mode label=""
    ;;
esac
